mapper.select:
SELECT role.*
FROM cat_role role
INNER JOIN cat_role_api re on role.id = re.role_id
INNER JOIN cat_api_path api on re.api_path = api.path and re.server = api.server

    api.server = #{server}
    and api.path = #{apiPath}

mapper.insert:
INSERT cat_role_api SELECT * FROM ( SELECT #{roleId},path,server,#{updateTime} FROM cat_api_path WHERE white_list = '0'
( server = #{relation.server} AND path = #{relation.apiPath} )
) insertTmp
